package com.sist.exam02;

public class StringTestToLowerCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String data = "Converts All of the characters in this string";
		String data2 = data.toLowerCase();
		String data3 = data.toUpperCase();
		System.out.println(data);	
		System.out.println(data2);	//전부소문자로변환
		System.out.println(data3);	//전부대문자로변환
	}

}
